// butter.js

export default class SmoothScroll {
    constructor() {
        this.defaults = {
            wrapperId: 'smoothScroll',
            wrapperDamper: 0.07,
            cancelOnTouch: false
        }
        this.wrapperDamper = 0.07;
        this.wrapperId = '';
        this.cancelOnTouch = false;
        this.wrapper = '';
        this.wrapperOffset = 0;
        this.animateId = '';
        this.resizing = false;
        this.active = false;
        this.wrapperHeight = 0;
        this.bodyHeight = 0;

        this.init();

    }
    validateOptions(ops) {
        for (let prop in ops) {
            if (this.defaults.hasOwnProperty(prop)) {
                Object.defineProperty(this.defaults, prop, { value: Object.getOwnPropertyDescriptor(ops, prop).value })
            }
        }
    }

    init(options) {
        if (options) {
            this.validateOptions(options);
        }

        this.active = true;
        this.resizing = false;
        this.wrapperDamper = this.defaults.wrapperDamper;
        this.wrapperId = this.defaults.wrapperId;
        this.cancelOnTouch = this.defaults.cancelOnTouch;

        this.wrapper = document.getElementById(this.wrapperId);
        this.wrapper.style.position = 'fixed';
        this.wrapper.style.width = '100%';

        this.wrapperHeight = this.wrapper.clientHeight;
        document.body.style.height = this.wrapperHeight + 'px';

        window.addEventListener('resize', this.resize.bind(this));
        if (this.cancelOnTouch) {
            window.addEventListener('touchstart', this.cancel.bind(this));
        }
        this.wrapperOffset = 0.0;
        this.animateId = window.requestAnimationFrame(this.animate.bind(this));
        document.body.scrollTop = document.documentElement.scrollTop = 0;
        this.wrapper.style.transform = 'translate3d(0, 0px, 0)';

        window.addEventListener('DOMContentLoaded', ()=>{
        });
        if (performance.navigation.type == performance.navigation.TYPE_RELOAD) {
            this.wrapperOffset = 0.0;
            // console.info("This page is reloaded");
            // document.body.scrollTo({
            //     top: '0'
            // });
        }
    }

    wrapperUpdate() {
        var scrollY = (document.scrollingElement != undefined) ? document.scrollingElement.scrollTop : (document.documentElement.scrollTop || 0.0);
        this.wrapperOffset += (scrollY - this.wrapperOffset) * this.wrapperDamper;
        this.wrapper.style.transform = 'translate3d(0,' + (-this.wrapperOffset.toFixed(2)) + 'px, 0)';
    }

    checkResize() {
        if (this.wrapperHeight != this.wrapper.clientHeight) {
            this.resize();
        }
    }

    resize() {
        var self = this;
        if (!self.resizing) {
            self.resizing = true;
            window.cancelAnimationFrame(self.animateId);
            window.setTimeout(function () {
                self.wrapperHeight = self.wrapper.clientHeight;
                if (parseInt(document.body.style.height) != parseInt(self.wrapperHeight)) {
                    document.body.style.height = self.wrapperHeight + 'px';
                }
                self.animateId = window.requestAnimationFrame(self.animate.bind(self));
                self.resizing = false;
            }, 150)
        }
    }

    animate() {
        this.checkResize();
        this.wrapperUpdate();
        this.animateId = window.requestAnimationFrame(this.animate.bind(this));
    }

    cancel() {
        if (this.active) {
            window.cancelAnimationFrame(this.animateId);

            window.removeEventListener('resize', this.resize);
            window.removeEventListener('touchstart', this.cancel);
            this.wrapper.removeAttribute('style');
            document.body.removeAttribute('style');

            this.active = false;
            this.wrapper = "";
            this.wrapperOffset = 0;
            this.resizing = true;
            this.animateId = "";
        }
    }
};
